import React,{ useContext } from "react";
import { FirstT } from "./Todo";


function Comp1() {
    const{tasks,settasks,task,settask,addtask,deletetask}=useContext(FirstT)
    return (
      <div>
          <h1 >ToDo - List</h1>
        
        <form action="">
          <h3> Next Job :</h3>  <input style={{textAlign:"center",width:"380px",paddingBottom:"7px"}} type="text"onChange={(e)=>{settask(e.target.value)}} name="" id="" value={task}/> <input type="button" onClick={addtask} value="Ajouter" id="aj" className='btn-primary'/>
        </form>
        <ul>
        {
            tasks.map((t)=>{
                return <li key={t.id}><FirstT setnotes={settasks} id={t.id} Task_done={t.done} dlt={()=>{deletetask(t.id)}} props={t.Job}/></li>           
            })
               
        }
        </ul>
      </div>
    );
  }
  
  export default Comp1;
