import React,{ useContext } from "react";
import { FirstT } from "./Todo";


function Comp1() {
    const{tasks,settasks,task,settask,addtask,deletetask,props,dlt,id,Task_done,setnotes,done,ref}=useContext(FirstT)
    return (
      <div>
          <h1 >ToDo - List</h1>
        
        <form action="">
          <h3> Next Job :</h3>  <input style={{textAlign:"center",width:"380px",paddingBottom:"7px"}} type="text"onChange={(e)=>{settask(e.target.value)}} name="" id="" value={task}/> <input type="button" onClick={addtask} value="Ajouter" id="aj" className='btn-primary'/>
        </form>
        <ul>
        {
            tasks.map((t)=>{
                return <li key={t.id}><FirstT setnotes={settasks} id={t.id} Task_done={t.done} dlt={()=>{deletetask(t.id)}} props={t.Job}/></li>           
            })
               
        }
        </ul>
        <input type="text" name="" id="inp" value={props} className={`${Task_done ? "rsi" : ""}`} ref={ref} disabled readOnly/>
        <input type="button" onClick={dlt} value="Supprimer" className='btn-danger'/>
        <input type="button" value="Done" id="do" onClick={e=>done(id)} className='btn-info'/>
      </div>
    );
  }
  
  export default Comp1;
